import { all, fork } from 'redux-saga/effects';
import { watchCartActions } from './cartSagas';
import { watchOrderActions } from './orderSagas';
import { watchProductActions } from './productSagas';
import { watchUserActions } from './userSagas';

export default function* rootSaga() {
  yield all([
    fork(watchCartActions),
    fork(watchOrderActions),
    fork(watchProductActions),
    fork(watchUserActions)
  ]);
}
