import React, { useState, useEffect } from 'react'
import {Row, Col, ListGroup, Image, Card, Button} from 'react-bootstrap'
import {useDispatch, useSelector} from 'react-redux'
import Message from '../components/Message'
import Loader from '../components/Loader'
import { Link } from 'react-router-dom'
import { getOrderDetails, deliverOrder } from '../actions/orderActions'
import {useHistory} from 'react-router-use-history'
import { useParams } from 'react-router-dom'
import axios from 'axios'
// import {PayPalButton} from 'react-paypal-button-v2'
import { ORDER_PAY_RESET, ORDER_DELIVER_RESET } from '../constants/orderConstants'
import Meta from '../components/Meta'

const OrderOrderScreen = () => {
    const dispatch = useDispatch()
    
    const {id} = useParams()
    const history = useHistory()
    const orderId = id
    const [sdkReady, setSdkReady] = useState(false)

    console.log(orderId)

    const orderDetails = useSelector(state=>state.orderDetails)
    const {order, loading, error} = orderDetails

    const orderPay = useSelector(state=>state.orderPay)
    const {loading:loadingPay, success:successPay} = orderPay

    const orderDeliver = useSelector(state=>state.orderDeliver)
    const {loading:loadingDeliver, success:successDeliver} = orderDeliver

    const userLogin = useSelector(state=>state.userLogin)
    const {userInfo} = userLogin

    if(!loading){
        const addDecimals = (num)=>{
            return (Math.round(num*100)/100).toFixed(2)
        }
        //calculate prices
        order.itemsPrice = addDecimals(order.orderItems.reduce((acc, item)=> acc+item.price*item.qty, 0).toFixed(2))    

    }

    

    useEffect(()=>{
        const addPayPalScript = async()=>{
            const {data: clientId} = await axios.get('/api/config/paypal')
            const script = document.createElement('script')
            script.type = 'text/javascript'
            script.src = `https://www.paypal.com/sdk/js?client-id=${clientId}`
            script.async = true
            script.onload=()=>{
                setSdkReady(true)
            }

            document.body.appendChild(script)
        }

        if(!order || successPay || successDeliver){
            dispatch({type:ORDER_DELIVER_RESET})
            dispatch({type:ORDER_PAY_RESET})
            dispatch(getOrderDetails(orderId))
        }else if(!order.isPaid){
            if(!window.paypal){
                addPayPalScript()
            }else{
                setSdkReady(true)
            }
        }

       
    }, [dispatch, orderId, successPay, successDeliver, order])

    useEffect(()=>{
        if(!userInfo){
            history.push('/login')
        }
    })

    // const successPaymentHandler = (paymentResult)=>{
    //     console.log(paymentResult)
    //     dispatch(payOrder(orderId, paymentResult))
    // }
    
    const deliverHandler=()=>{
        dispatch(deliverOrder(order))
    }

  return loading ? <Loader />:error?<Message>{error}</Message>:<>
    <h1>Order {order._id}</h1>
    <Meta title='Order Details' />
    <Row>
        <Col md={8}>
            <ListGroup variant='flush'>
                <ListGroup.Item>
                    <h2>Shipping</h2>
                   <p> <strong>Name:</strong>{order.user.name}</p>
                   <p> <strong>Email:</strong> <a href={`mail to ${order.user.email}`}>{order.user.email}</a></p>
                    <p>
                        <strong>Address:</strong>
                       {' '} {order.shippingAddress.address}, {order.shippingAddress.city}{' '}
                        {order.shippingAddress.postalCode}, {' '}
                        {order.shippingAddress.country}
                    </p>
                    {order.isDelivered?<Message variant='success'>Delivered on {order.deliveredAt}</Message>:<Message variant='danger'>Not Delivered</Message>}
                </ListGroup.Item>

                <ListGroup.Item>
                    <h2>Payment Method</h2>
                    <p>
                    <strong>Method: </strong>
                    {order.paymentMethod}
                    </p>
                    {order.isPaid?<Message variant='success'>Paid on {order.paidAt}</Message>:<Message variant='danger'>Not Paid</Message>}
                </ListGroup.Item>

                <ListGroup.Item>
                    <h2>Order Items</h2>
                    {order.orderItems.length===0 ? <Message>Order is Empty</Message>
                    :
                    <ListGroup variant='flush'>
                        {order.orderItems.map((item, index)=>(
                            <ListGroup.Item key={index}>
                                <Row>
                                    <Col md={1}>
                                        <Image src={item.image} alt={item.name} fluid rounded />
                                    </Col>
                                    <Col>
                                    <Link to={`/product/${item.product}`}>
                                        {item.name}
                                    </Link>
                                    </Col>
                                    <Col md={4}>
                                        {item.qty} X {item.price} = {`\u20B9 `}{item.qty*item.price}
                                    </Col>
                                </Row>
                            </ListGroup.Item>
                        ))}
                    </ListGroup>
                    }
                </ListGroup.Item>
            </ListGroup>
        </Col>
        <Col md={4}>
            <Card>
                <ListGroup variant='flush'>
                    <ListGroup.Item>
                        <h2>Order Summary</h2>
                    </ListGroup.Item>
                    <ListGroup.Item>
                        <Row>
                            <Col>Items </Col>
                            <Col>{`\u20B9 `}{order.itemsPrice}</Col>
                        </Row>
                    </ListGroup.Item>
                    <ListGroup.Item>
                        <Row>
                            <Col>Shipping</Col>
                            <Col>{`\u20B9 `}{order.shippingPrice}</Col>
                        </Row>
                    </ListGroup.Item>
                    <ListGroup.Item>
                        <Row>
                            <Col>Tax</Col>
                            <Col>{`\u20B9 `}{order.taxPrice}</Col>
                        </Row>
                    </ListGroup.Item>
                    <ListGroup.Item>
                        <Row>
                            <Col>Total</Col>
                            <Col>{`\u20B9 `}{order.totalPrice}</Col>
                        </Row>
                    </ListGroup.Item>
                    {!order.isPaid && (
                        <ListGroup.Item>
                            {loadingPay && <Loader/>}
                            {!sdkReady ? <Loader/> : ( <Button>Pay</Button>
                                // <PayPalButton amount={order.totalPrice} onSuccess={successPaymentHandler} />
                            )}
                        </ListGroup.Item>
                    )}
                    {loadingDeliver && <Loader />}
                    {userInfo && userInfo.isAdmin && order.isPaid && !order.isDelivered && (
                        <ListGroup.Item>
                            <Button type='button' className='btn btn-block' onClick={deliverHandler}>
                                Mark As Delivered
                            </Button>
                        </ListGroup.Item>
                    )}
                    </ListGroup>
            </Card>
        </Col>
      </Row>
  </>
}

export default OrderOrderScreen
